import{_ as l,c as i,o as e,a as n}from"./app.65ba7c0f.js";const h='{"title":"\u7740\u8272","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7740\u8272","slug":"\u7740\u8272-1"},{"level":2,"title":"\u56FE\u5F62(\u5B9E\u65F6\u6E32\u67D3)\u7BA1\u7EBF","slug":"\u56FE\u5F62-\u5B9E\u65F6\u6E32\u67D3-\u7BA1\u7EBF"},{"level":2,"title":"\u7EB9\u7406\u6620\u5C04","slug":"\u7EB9\u7406\u6620\u5C04"},{"level":3,"title":"\u7EB9\u7406\u653E\u5927\u95EE\u9898(Texture Magnification)","slug":"\u7EB9\u7406\u653E\u5927\u95EE\u9898-texture-magnification"},{"level":3,"title":"\u7EB9\u7406\u5E94\u7528","slug":"\u7EB9\u7406\u5E94\u7528"}],"relativePath":"MOOC/Games101/07-09_shading.md"}',a={},s=n(`<h1 id="\u7740\u8272" tabindex="-1">\u7740\u8272</h1><h2 id="\u7740\u8272-1" tabindex="-1">\u7740\u8272</h2><ul><li>\u7814\u7A76\u5149\u7EBF\u4E0E\u6750\u8D28\u5982\u4F55\u4F5C\u7528\u5C31\u662F\u7740\u8272</li><li>\u7740\u8272!=\u5149\u5F71\uFF0C\u53EA\u8003\u8651\u5149\u5BF9\u7269\u4F53\u672C\u8EAB\u7684\u5F71\u54CD</li><li>Blinn-Phong \u53CD\u5C04\u6A21\u578B <ul><li>\u7740\u8272\u70B9 <ul><li>\u89C2\u6D4B\u65B9\u5411<code>v hat</code></li><li>\u6CD5\u7EBF<code>n hat</code></li><li>\u5149\u7167\u65B9\u5411<code>l hat</code></li><li>\u8868\u9762\u53C2\u6570(\u989C\u8272\uFF0Cshininess)</li></ul></li><li>\u9AD8\u5149(\u955C\u9762\u53CD\u5C04\uFF0C<code>specular reflect</code>) <ul><li>\u534A\u7A0B\u5411\u91CF\u79BB\u6CD5\u5411\u91CF\u8D8A\u8FD1\uFF0C\u8BF4\u660E\u8D8A\u63A5\u8FD1\u955C\u9762\u53CD\u5C04\u5149</li><li>\u534A\u7A0B\u5411\u91CF\uFF1A<code>h = bisector(v, l) = (v+l)/|v+l|</code></li><li>\u516C\u5F0F\uFF1A<code>L_s = k_s(I/r^2)max(0,n\xB7h)^p</code><ul><li><code>p</code>\u7528\u6765\u63A7\u5236\u9AD8\u5149\u7684\u5927\u5C0F</li></ul></li></ul></li><li>\u6F2B\u53CD\u5C04(<code>diffuse reflect</code>) <ul><li>\u5149\u5C04\u5230\u7740\u8272\u70B9\u4E0A\u5E76\u5411\u56DB\u9762\u516B\u65B9\u53CD\u5C04\uFF0C\u6548\u679C\u4E0E\u89C2\u5BDF\u65B9\u5411\u65E0\u5173</li><li>\u7406\u8BBA <ul><li>Lambert\u4F59\u5F26\u5B9A\u7406\uFF1A<code>l hat</code>\u548C<code>n hat</code>\u5939\u89D2\u51B3\u5B9A\u5149\u7167\u5F3A\u5EA6\uFF0C<code>L = cos\u03B8 = l hat * n hat</code></li><li>\u80FD\u91CF\u8870\u9000\u5B9A\u7406\uFF1Ar\u5904\u7684\u80FD\u91CF\u5F3A\u5EA6\uFF1A<code>I/r^2</code></li></ul></li><li>Lambert\u6F2B\u53CD\u5C04\u6A21\u578B\uFF1A<code>L_d=k_d(I/r^2)max(0,n\xB7l)</code><ul><li><code>L_d</code>\uFF1A\u6F2B\u53CD\u5C04\u7684\u5149</li><li><code>k_d</code>\uFF1A\u6F2B\u53CD\u5C04\u7CFB\u6570</li><li><code>I/r^2</code>\uFF1A\u5230\u8FBE\u6BCF\u4E2A\u7740\u8272\u70B9\u4E0A\u7684\u80FD\u91CF</li><li><code>max(0,n\xB7l)</code>\uFF1A\u6BCF\u4E2A\u7740\u8272\u70B9\u5438\u6536\u7684\u80FD\u91CF\uFF0C<code>n</code>\u4E3A\u6CD5\u5411\u91CF\uFF0C<code>l</code>\u4E3A\u5165\u5C04\u5149\u7684\u5355\u4F4D\u5411\u91CF <ul><li>\u6F2B\u53CD\u5C04\u7684\u5149\u53EA\u4E0E\u5165\u5C04\u89D2\u5EA6\u76F8\u5173</li></ul></li></ul></li></ul></li><li>\u73AF\u5883\u5149\u7167(<code>Ambient light</code>) <ul><li><strong>\u5927\u80C6\u7B80\u5316\uFF0C\u6DF1\u5165\u7684\u9700\u8981\u6D89\u53CA\u5168\u5C40\u5149\u7167</strong></li><li>\u516C\u5F0F\uFF1A<code>L_a = k_aI_a</code>\uFF0C\u4E0E\u5165\u5C04\u548C\u53CD\u5C04\u5149\u89D2\u5EA6\u90FD\u6CA1\u6709\u5173\u7CFB\uFF0C\u6240\u4EE5\u662F\u4E00\u4E2A\u5E38\u6570</li></ul></li><li>\u603B\u7ED3 <ul><li><code>L = L_a + L_d + L_s</code></li><li><code> = k_aI_a + k_d(I/r^2)max(0,n\xB7l) + k_s(I/r^2)max(0,n\xB7h)^p</code></li></ul></li></ul></li><li>\u7740\u8272\u9891\u7387 <ul><li>flat shading\uFF1Ashade each face</li><li>gouraud shading\uFF1Ashade each vertex <ul><li>\u76F8\u90BB\u9762\u6CD5\u7EBF\u7684\u52A0\u6743\u5E73\u5747\u4E3A\u9876\u70B9\u6CD5\u7EBF</li></ul></li><li>phong shading\uFF1Ashade each pixel</li></ul></li></ul><h2 id="\u56FE\u5F62-\u5B9E\u65F6\u6E32\u67D3-\u7BA1\u7EBF" tabindex="-1">\u56FE\u5F62(\u5B9E\u65F6\u6E32\u67D3)\u7BA1\u7EBF</h2><ul><li>pipeline\uFF1A <ul><li><code>application-&gt;[vertex processing]</code></li><li><code>-vertex stream-&gt;[triangle processing]</code></li><li><code>-triangle stream-&gt;[rasterization]</code></li><li><code>-fragment stream-&gt;[fragment processing]</code></li><li><code>-shaded fragment-&gt;[framebuffer operations]</code></li><li><code>-&gt;display</code></li></ul></li><li>shader program <ul><li>\u5BF9\u9876\u70B9\u548C\u50CF\u7D20\u5904\u7406\u9636\u6BB5\u8FDB\u884C\u7F16\u7A0B</li><li>\u63CF\u8FF0\u5728\u6BCF\u4E2A\u9876\u70B9\u6216\u50CF\u7D20\u4E0A\u90FD\u4F1A\u6267\u884C\u7684\u64CD\u4F5C</li><li>glsl\u7EC3\u4E60\u7F51\u7AD9 <ul><li><a href="https://www.shadertoy.com/" target="_blank" rel="noopener noreferrer">shadertoy</a><ul><li><a href="https://www.bilibili.com/video/BV1Xm4y1X7qU" target="_blank" rel="noopener noreferrer">\u6559\u7A0B</a></li><li><a href="https://iquilezles.org/articles/" target="_blank" rel="noopener noreferrer">iq\u5927\u4F6C\u535A\u5BA2</a></li></ul></li><li><a href="https://blog.csdn.net/misol/article/details/7658949" target="_blank" rel="noopener noreferrer">glsl\u8BED\u6CD5\u57FA\u7840</a></li></ul></li></ul></li></ul><h2 id="\u7EB9\u7406\u6620\u5C04" tabindex="-1">\u7EB9\u7406\u6620\u5C04</h2><ul><li>\u5B9A\u4E49\u4EFB\u610F\u70B9\u7684\u4E0D\u540C\u5C5E\u6027\uFF0C\u8BA9\u540C\u4E00\u4E2A\u5149\u7167\u6A21\u578B\u4E0B\u70B9\u7684\u989C\u8272\u4E0D\u540C</li><li>\u7EB9\u7406 <ul><li>\u5C06\u4E8C\u7EF4\u5E73\u9762\u8986\u76D6\u5728\u4E09\u7EF4\u7269\u4F53\u7684\u8868\u9762\u7684\u8FC7\u7A0B</li><li>\u7EB9\u7406\u5750\u6807uv</li></ul></li><li>\u5E94\u7528\u7EB9\u7406 <ul><li>\u5229\u7528\u76EE\u6807\u70B9\u5728\u4E09\u89D2\u5F62\u5185\u7684\u4F4D\u7F6E\u6620\u5C04\u5230\u4E8C\u7EF4\u4E0A\u7684\u4F4D\u7F6E\u6765\u67E5\u8BE2\u5176\u5728uv\u4E0A\u7684\u5BF9\u5E94\u4F4D\u7F6E\u989C\u8272</li><li>\u7136\u540E\u4F7F\u7528\u83B7\u53D6\u5230\u7684\u989C\u8272\u4EE3\u66FF\u7740\u8272\u6A21\u578B\u4E2D\u7684<code>k_d</code>\u6765\u5F71\u54CD\u6700\u7EC8\u6548\u679C</li></ul></li><li><a href="https://zhuanlan.zhihu.com/p/448575965" target="_blank" rel="noopener noreferrer">\u4E09\u89D2\u5F62\u91CD\u5FC3\u63D2\u503C\u4E0E\u900F\u89C6\u6821\u6B63\u63D2\u503C</a></li></ul><h3 id="\u7EB9\u7406\u653E\u5927\u95EE\u9898-texture-magnification" tabindex="-1">\u7EB9\u7406\u653E\u5927\u95EE\u9898(Texture Magnification)</h3><ul><li>\u7EB9\u7406\u8FC7\u5C0F\uFF1A\u5C06\u4E00\u5F20\u4F4E\u5206\u8FA8\u7387\u7684\u7EB9\u7406\u8D34\u5728\u4E00\u4E2A\u9AD8\u5206\u8FA8\u7387\u7684\u5C4F\u5E55\u4E0A\u65F6\uFF0C\u4F1A\u51FA\u73B0\u67E5\u8BE2\u5230\u7684uv\u5750\u6807\u4E0D\u662F\u6574\u6570\u7684\u95EE\u9898\uFF0C\u5982\u4F55\u89E3\u51B3\uFF1F <ul><li>\u6700\u8FD1\u90BB\u63D2\u503C(Nearest)\uFF1A\u5BF9\u5750\u6807\u8FDB\u884C\u56DB\u820D\u4E94\u5165\u67E5\u8BE2\u76F8\u8FD1\u5750\u6807\u7684\u989C\u8272</li><li>\u53CC\u7EBF\u6027\u63D2\u503C(Bilinear Interpolation)\uFF1A\u5BF9\u90BB\u8FD1\u56DB\u4E2A\u50CF\u7D20\u53D6\u4E24\u4E2A\u65B9\u5411\u4E0A\u7684\u7EBF\u6027\u63D2\u503C\u5F97\u5230\u5E73\u5747\u503C</li><li>Bicubic\uFF1A\u5BF9\u5468\u56F4\u5341\u516D\u4E2A\u8FDB\u884C\u7EBF\u6027\u63D2\u503C\u5F97\u5230\u5E73\u5747\u503C</li></ul></li><li>\u7EB9\u7406\u8FC7\u5927\uFF1A\u5982\u679C\u7EB9\u7406\u592A\u5927\u4E86\u5C31\u4F1A\u51FA\u73B0\u8D70\u6837\u95EE\u9898\uFF0C\u5982\u4F55\u89E3\u51B3\uFF1F <ul><li>\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0\u8D70\u6837\u73B0\u8C61\uFF1F\u79BB\u76F8\u673A\u8D8A\u8FDC\u7684\u7269\u4F53\u5C31\u4F1A\u8986\u76D6\u8D8A\u591A\u7684texel\uFF0C\u4E5F\u5C31\u662F\u8BF4\u8FDC\u5904\u7684\u7269\u4F53\u5728\u91C7\u53D6\u7EB9\u7406\u65F6\u4F1A\u51FA\u73B0\u91CD\u53E0\u73B0\u8C61(\u91C7\u6837\u7387\u4E0D\u591F)\uFF0C\u5BFC\u81F4\u8D70\u6837</li><li>\u6709\u4EC0\u4E48\u95EE\u9898 <ul><li>\u89C6\u89C9\uFF1A\u8D70\u6837\uFF0C\u4E22\u5931\u4FE1\u606F</li><li>\u6027\u80FD\uFF1AGPU\u4E0D\u80FD\u5B8C\u6574\u8BFB\u53D6\u6240\u9700texel\uFF0C\u5BFC\u81F4\u6027\u80FD\u4F4E\u6548</li></ul></li><li>\u89E3\u51B3\u65B9\u6848 <ul><li>MSAA\uFF1A\u4E0E\u5149\u6805\u5316\u6297\u952F\u9F7F\u4E00\u6837\u8FDB\u884C\u8D85\u91C7\u6837\uFF0C\u4F46\u662F\u8D39\u7528\u9AD8\u6602</li><li>Mipmap\uFF1A\u5B9E\u73B0\u8303\u56F4\u67E5\u8BE2</li></ul></li></ul></li></ul><h4 id="mipmap" tabindex="-1">Mipmap</h4><ul><li>\u6838\u5FC3\u60F3\u6CD5\uFF1A\u79BB\u7EBF\u9884\u5904\u7406\u67E5\u8BE2\u6BCF\u4E2Afootprint\u5BF9\u5E94\u533A\u57DF\u91CC\u7684\u989C\u8272\u5747\u503C\uFF0C\u5728\u6E32\u67D3\u524D\u751F\u6210</li><li>level <ul><li>\u56E0\u4E3A\u4E00\u4E2A\u573A\u666F\u4E2D\u5B58\u5728\u4E0D\u540C\u5927\u5C0F\u7684footprint\uFF0C\u9700\u8981\u4E3A\u4E0D\u540C\u5927\u5C0F\u7684footprint\u51C6\u5907\u4E0D\u540C\u7CBE\u5EA6\u7684mipmap\uFF0C\u79F0\u4E3ALevel <ul><li>\u8D8A\u9AD8\u7684level\u5C31\u4EE3\u8868\u4E86\u66F4\u5927\u7684footprint\u7684\u533A\u57DF\u67E5\u8BE2</li><li>\u79F0\u4E00\u4E2Apixel\u8986\u76D6\u4E0D\u540C\u6570\u91CF\u7684texel\u7684\u73B0\u8C61\u4E3Afootprint</li></ul></li><li>Level\u7684\u5C42\u6570\uFF1A<code>log2n</code></li><li>\u5B58\u50A8\u91CF\uFF1A<code>(4/3) = (1+1/4+1/16...)</code></li></ul></li><li>\u5FEB\u4F46\u662F\u4E0D\u51C6\u4E14\u53EA\u80FD\u505A\u8FD1\u4F3C\u6B63\u65B9\u5F62\u7684\u67E5\u8BE2</li><li>\u8BA1\u7B97 <ul><li>\u5728\u5C4F\u5E55\u7A7A\u95F4\u4E2D\u53D6\u5F53\u524D\u50CF\u7D20\u70B9\u7684\u53F3\u65B9\u548C\u4E0A\u65B9\u7684\u4E24\u4E2A\u76F8\u90BB\u50CF\u7D20\u70B9(4\u4E2A\u5168\u53D6\u4E5F\u53EF\u4EE5)</li><li>\u5206\u522B\u67E5\u8BE2\u5F97\u5230\u5F53\u524D\u50CF\u7D20\u70B9\u4E0E\u53F3\u65B9\u50CF\u7D20\u70B9\u548C\u4E0A\u65B9\u50CF\u7D20\u70B9\u5BF9\u5E94\u5728Texture space\u7684\u5750\u6807\uFF0C\u8BA1\u7B97\u51FA\u8FD93\u4E2A\u70B9\u5728Texture space\u7684\u8DDD\u79BB\uFF0C\u4E8C\u8005\u53D6\u6700\u5927\u503C<code>L</code></li><li>\u90A3\u4E48levelD\u5C31\u662F\u8FD9\u4E2A\u8DDD\u79BB\u7684log2\u503C (<code>D = log2L</code>)</li><li>\u5904\u7406D <ul><li>\u56DB\u820D\u4E94\u5165\u53D6\u5F97\u6700\u8FD1\u7684\u90A3\u4E2Alevel D</li><li>\u5229\u7528D\u503C\u5728 \u5411\u4E0B\u548C\u5411\u4E0A\u53D6\u6574\u7684\u4E24\u4E2A\u4E0D\u540Clevel\u8FDB\u884C3\u7EBF\u6027\u63D2\u503C <ul><li>\u4E09\u7EBF\u6027\u63D2\u503C\uFF1A\u5728\u4E24\u4E2A\u5C42(<code>D</code>,<code>D+1</code>)\u5206\u522B\u4F5C\u53CC\u7EBF\u6027\u63D2\u503C\u7684\u57FA\u7840\u4E0A\uFF0C\u518D\u5728\u5C42\u4E0E\u5C42\u4E4B\u95F4\u518D\u505A\u4E00\u6B21\u63D2\u503C</li></ul></li></ul></li></ul></li><li>\u5C40\u9650\u6027\uFF1A\u53EA\u80FD\u9884\u5904\u7406\u51FA\u6B63\u65B9\u5F62\u533A\u57DF\u7684\u5747\u503C\uFF0C\u8FD9\u5BFC\u81F4\u8FDC\u5904\u8FC7\u5EA6\u6A21\u7CCA <ul><li>\u5404\u5411\u5F02\u6027\u8FC7\u6EE4\uFF08Ripmaps\uFF09 <ul><li>\u5141\u8BB8\u5BF9\u957F\u6761\u7684\u533A\u57DF\u8FDB\u884C\u8303\u56F4\u67E5\u8BE2\uFF0C\u4F46\u662F\u4E0D\u80FD\u7528\u4E8E\u659C\u7740\u7684\u533A\u57DF</li><li>\u751F\u6210\u5404\u5411\u5F02\u6027\u8FC7\u6EE4\u7684\u56FE\u7684\u5F00\u9500\u662F\u539F\u672C\u7684<strong>\u4E09\u500D</strong></li><li>\u5404\u5411\u5F02\u6027\u7684\u610F\u601D\u662F\uFF0C\u5728\u4E0D\u540C\u7684\u65B9\u5411\u4E0A\u5B83\u7684\u8868\u73B0\u5404\u4E0D\u76F8\u540C</li><li>\u5404\u5411\u5F02\u6027\u7684\u51E0X\u662F\u538B\u7F29\u51E0\u500D\uFF0C\u4E5F\u5C31\u662F\u4ECE\u5DE6\u4E0A\u89D2\u5F80\u53F3\u4E0B\u89D2\u591A\u51E0\u5C42</li></ul></li></ul></li></ul><h3 id="\u7EB9\u7406\u5E94\u7528" tabindex="-1">\u7EB9\u7406\u5E94\u7528</h3><ul><li>\u73AF\u5883\u5149\u7167(\u8D34\u56FE)\uFF0C<code>Environment Map</code><ul><li>\u5C06\u73AF\u5883\u5149\u5B58\u50A8\u5728\u4E00\u4E2A\u8D34\u56FE\u4E0A <ul><li>\u95EE\u9898\uFF1A\u5C55\u5F00\u540E\u7684\u7EB9\u7406\u4F1A\u51FA\u73B0\u626D\u66F2</li></ul></li><li>\u5929\u7A7A\u76D2\uFF1A<code>Cube Map</code><ul><li>\u7528\u516D\u5E45texture\u8868\u793A\u4E00\u4E2A\u5929\u7A7A\u76D2</li><li>\u5229\u7528\u65B9\u5411\u8BA1\u7B97\u51FA\u4E0E\u5BF9\u5E94\u5E73\u9762\u4E0A\u7684\u4EA4\u70B9\u5750\u6807\uFF0C\u5254\u9664\u5E73\u9762\u6240\u5BF9\u5E94\u7684\u4E00\u7EF4\uFF0C\u5269\u4E0B\u6765\u7684\u4E24\u7EF4\u5750\u6807\u8F6C\u6362\u5230(0,1)\u8303\u56F4\u4E4B\u5185\u5373\u4E3A(u,v)\u5750\u6807</li></ul></li></ul></li><li>\u51F9\u51F8\u8D34\u56FE\uFF0C<code>Bump Mapping</code><ul><li>texture\u9664\u4E86\u8BB0\u5F55\u989C\u8272\u5916\u8FD8\u53EF\u4EE5\u8BB0\u5F55\u5176\u4ED6\u4FE1\u606F\uFF0C\u6BD4\u5982\u4E0E\u7269\u4F53\u8868\u9762\u7684\u76F8\u5BF9\u9AD8\u5EA6</li><li>\u5BF9\u6BCF\u4E2Apixel\u505A\u4E00\u4E2A\u201C\u6270\u52A8\u201D\uFF0C\u5229\u7528\u9AD8\u5EA6\u5DEE\u91CD\u65B0\u8BA1\u7B97\u6CD5\u7EBF(\u53EA\u662F\u6270\u52A8\u6CD5\u7EBF\u65B9\u5411\uFF0C\u6CA1\u6709\u79FB\u52A8\u9876\u70B9) <ul><li><img src="https://www.yuque.com/api/filetransfer/images?url=https%3A%2F%2Fddddyx.oss-cn-beijing.aliyuncs.com%2Fimg%2F202207242042867.png&amp;sign=7b03a2c8a51bfc8872d0afd784d20a498bfa10811477bb4a28d08f358e90146d" alt=""></li></ul></li><li>\u8BA1\u7B97\u865A\u62DF\u6CD5\u7EBF <ul><li>\u5E73\u9762\uFF1A<img src="https://www.yuque.com/api/filetransfer/images?url=https%3A%2F%2Fddddyx.oss-cn-beijing.aliyuncs.com%2Fimg%2F202207242042868.png&amp;sign=bf19d57d44bc285b15472970ee8d3631aa85e0bce4b6e14c551a425e4fa33739" alt=""></li><li>3D\uFF1A<img src="https://www.yuque.com/api/filetransfer/images?url=https%3A%2F%2Fddddyx.oss-cn-beijing.aliyuncs.com%2Fimg%2F202207242042869.png&amp;sign=a0386e8bb159e156aab6e9b7394eb9aa0fd55ae7090d1d9abc91a57eec2b7000" alt=""></li></ul></li></ul></li><li>\u4F4D\u79FB\u8D34\u56FE\uFF0C<code>Displacement Mapping</code><ul><li>\u4E0EBump Mapping\u7C7B\u4F3C\uFF0C\u4F46\u662F\u4E0E\u51F9\u51F8\u8D34\u56FE\u4E0D\u540C\uFF0C\u4F4D\u79FB\u8D34\u56FE\u771F\u6B63\u79FB\u52A8\u4E86\u9876\u70B9</li><li>\u53EF\u4EE5\u4ECE\u5F71\u5B50\u4E2D\u770B\u51FA\u533A\u522B</li><li>\u4EE3\u4EF7\uFF1A\u9700\u8981\u6A21\u578B\u7CBE\u5EA6\u6BD4\u7EB9\u7406\u76F8\u7B49\u751A\u81F3\u66F4\u9AD8</li></ul></li><li>\u4E09\u7EF4\u7EB9\u7406 <ul><li>\u5B9A\u4E49\u7A7A\u95F4\u4E2D\u4EFB\u610F\u4E00\u70B9\u7684\u7EB9\u7406</li><li>\u5E76\u6CA1\u6709\u771F\u6B63\u751F\u6210\u7EB9\u7406\u7684\u56FE\uFF0C\u800C\u662F\u5B9A\u4E49\u4E00\u4E2A\u4E09\u7EF4\u7A7A\u95F4\u7684\u566A\u58F0\u51FD\u6570\u7ECF\u8FC7\u5404\u79CD\u5904\u7406\uFF0C\u53D8\u6210\u9700\u8981\u7684\u6837\u5B50</li></ul></li><li>\u9634\u5F71\u7EB9\u7406\uFF1A\u9634\u5F71\u53EF\u4EE5\u8BA1\u7B97\u597D\uFF0C\u76F4\u63A5\u5199\u5728Texture\u91CC</li></ul><h4 id="\u51F9\u51F8\u8D34\u56FE\u548C\u4F4D\u79FB\u8D34\u56FE\u7684\u4F2A\u4EE3\u7801\u5B9E\u73B0" tabindex="-1">\u51F9\u51F8\u8D34\u56FE\u548C\u4F4D\u79FB\u8D34\u56FE\u7684\u4F2A\u4EE3\u7801\u5B9E\u73B0</h4><div class="language-txt line-numbers-mode"><pre><code>// Bumping Mapping
Let n = normal = (x, y, z)
Vector t = (x*y/sqrt(x*x+z*z),sqrt(x*x+z*z),z*y/sqrt(x*x+z*z))
Vector b = n cross product t
Matrix TBN = [t b n]
dU = kh * kn * (h(u+1/w,v)-h(u,v))
dV = kh * kn * (h(u,v+1/h)-h(u,v))
Vector ln = (-dU, -dV, 1)
// \u6270\u52A8\u540E\u7684\u6CD5\u5411\u91CF
Normal n = normalize(TBN * ln)

// Displacement Mapping
Let n = normal = (x, y, z)
Vector t = (x*y/sqrt(x*x+z*z),sqrt(x*x+z*z),z*y/sqrt(x*x+z*z))
Vector b = n cross product t
Matrix TBN = [t b n]
dU = kh * kn * (h(u+1/w,v)-h(u,v))
dV = kh * kn * (h(u,v+1/h)-h(u,v))
Vector ln = (-dU, -dV, 1)
// \u6270\u52A8\u540E\u7684\u9876\u70B9\u4E0E\u6CD5\u5411\u91CF
Position p = p + kn * n * h(u,v)
Normal n = normalize(TBN * ln)
</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br></div></div>`,15),r=[s];function t(o,c,d,u,p,b){return e(),i("div",null,r)}var g=l(a,[["render",t]]);export{h as __pageData,g as default};
