import{_ as l,c as i,o as u,a as t}from"./app.4b84d482.js";const m='{"title":"\u4FE1\u606F\u7684\u8F93\u5165","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u611F\u89C9","slug":"\u611F\u89C9"},{"level":2,"title":"\u77E5\u89C9","slug":"\u77E5\u89C9"},{"level":2,"title":"\u8BB0\u5FC6","slug":"\u8BB0\u5FC6"},{"level":2,"title":"\u601D\u7EF4\u4E0E\u89E3\u51B3\u95EE\u9898","slug":"\u601D\u7EF4\u4E0E\u89E3\u51B3\u95EE\u9898"}],"relativePath":"MOOC/IntroductiontoPsychology/03\u8BA4\u77E5\u8FC7\u7A0B.md"}',e={},a=t('<h1 id="\u4FE1\u606F\u7684\u8F93\u5165" tabindex="-1">\u4FE1\u606F\u7684\u8F93\u5165</h1><h2 id="\u611F\u89C9" tabindex="-1">\u611F\u89C9</h2><ul><li>\u57FA\u672C\u6982\u5FF5 <ul><li>\u5B9A\u4E49\uFF1A\u4EBA\u8111\u5BF9\u76F4\u63A5\u4F5C\u7528\u4E8E\u611F\u89C9\u5668\u5B98\u7684\u523A\u6FC0\u7269\u7684\u53CD\u6620\uFF0C\u611F\u53D7\u662F\u4E00\u9879\u5927\u8111\u673A\u80FD\u3002</li><li>\u5206\u7C7B <ul><li>\u5185\u90E8\uFF1A\u52A8\u611F\uFF0C\u5E73\u8861\u611F\uFF0C\u5185\u810F\u89C9</li><li>\u5916\u90E8\uFF1A\u89C6\uFF0C\u542C\uFF0C\u5473\uFF0C\u55C5\uFF0C\u80A4(\u89E6\u89C9\uFF0C\u538B\u611F\uFF0C\u75DB\u89C9\uFF0C\u6E29\u89C9)</li></ul></li><li>\u611F\u89C9\u7684\u91CF\u5316\uFF1A\u5FC3\u7406\u7269\u7406\u5B66 <ul><li>\u611F\u89C9\u9608\u9650\uFF1A\u7EDD\u5BF9\u9608\u9650\uFF0C\u5DEE\u522B\u9608\u9650</li><li>\u611F\u89C9\u6027\u4E0E\u611F\u89C9\u9608\u503C\u5448\u53CD\u6BD4\u5173\u7CFB</li><li>\u6700\u5C0F\u53EF\u89C9\u5DEE\uFF1A\u6307\u88AB\u6D4B\u8BD5\u8005\u6709\u5927\u4E8E50%\u7684\u51E0\u7387\u63D0\u51FA\u6709\u611F\u89C9\u7684\u6700\u5C0F\u7684\u523A\u6FC0\u53D8\u5316\u91CF\u3002</li></ul></li></ul></li><li>\u91CD\u8981\u6027 <ul><li>\u611F\u89C9\u662F\u5EFA\u7ACB\u590D\u6742\u7ECF\u9A8C\u7684\u57FA\u672C\u8FC7\u7A0B</li><li>\u8BC1\u660E\uFF1A\u611F\u89C9\u5265\u593A\uFF0C\u529F\u80FD\u53D7\u635F(\u8272\u76F2),\u611F\u89C9\u8865\u507F</li></ul></li></ul><h2 id="\u77E5\u89C9" tabindex="-1">\u77E5\u89C9</h2><ul><li>\u57FA\u672C\u6982\u5FF5 <ul><li>\u5B9A\u4E49\uFF1A\u76F4\u63A5\u4F5C\u7528\u4E8E\u611F\u89C9\u5668\u5B98\u7684\u4E8B\u7269\u5728\u8111\u4E2D\u7684\u6574\u4F53\u53CD\u6620\uFF0C\u662F\u5927\u8111\u5BF9\u611F\u89C9\u5230\u7684\u4FE1\u606F\u7684\u7EC4\u7EC7\u548C\u89E3\u91CA\u7684\u8FC7\u7A0B\u3002</li><li>\u5206\u7C7B <ul><li>\u4EE5\u5BF9\u8C61\u6765\u5206 \uFF1A\u793E\u4F1A\u77E5\u89C9\uFF0C\u7269\u4F53\u77E5\u89C9</li><li>\u4EE5\u4E8B\u7269\u7279\u6027\u6765\u5206\uFF1A\u7A7A\u95F4\uFF0C\u65F6\u95F4\uFF0C\u8FD0\u52A8</li><li>\u4EE5\u611F\u53D7\u5668\u6765\u5206 \uFF1A\u89C6\uFF0C\u542C\uFF0C\u55C5...</li></ul></li></ul></li><li>\u5F62\u6210\u8FC7\u7A0B <ol><li>\u611F\u89C9\u4FE1\u606F</li><li>\u7EC4\u7EC7\u4FE1\u606F</li><li>\u8FA8\u8BA4\u53CA\u8BC6\u522B\u4FE1\u606F</li></ol></li><li>\u76F8\u5173\u7406\u8BBA <ul><li>\u8D6B\u5C14\u59C6\u970D\u5179\u7406\u8BBA\uFF1A\u5F3A\u8C03\u8FC7\u53BB\u7684\u7ECF\u9A8C\u5BF9\u611F\u77E5\u7684\u5F71\u54CD</li><li>\u683C\u5F0F\u5854\u5FC3\u7406\u5B66\uFF1A\u5F3A\u8C03\u6574\u4F53\u4E0D\u540C\u4E8E\u90E8\u5206\u7684\u603B\u548C\uFF0C\u5982\u6211\u4EEC\u5BF9\u82B1\u7684\u611F\u77E5\u4E0D\u4F46\u5305\u62EC\u5B83\u7684\u989C\u8272,\u5F62\u72B6\uFF0C\u8FD8\u6709\u8FC7\u53BB\u7684\u5370\u8C61\uFF0C\u8FD9\u4E9B\u52A0\u8D77\u6765\u624D\u662F\u6211\u4EEC\u5BF9\u82B1\u7684\u5B8C\u6574\u611F\u77E5\u3002</li><li>\u533A\u522B <ul><li>\u6BD4\u5982\u4F60\u770B\u4E86\u4EF6\u662F\u4F60\u5DE5\u8D44\u51E0\u500D\u7684\u8863\u670D\u3002</li><li>\u683C\u5F0F\u5854\u6839\u636E\u4EF7\u683C\u544A\u8BC9\u4F60\uFF1A\u8FD9\u8863\u670D\u7528\u5F97\u7B97\u662F\u4E0A\u7B49\u7684\u6750\u6599\u3002</li><li>\u8D6B\u5C14\u59C6\u970D\u8328\u4E00\u770B\u5230\u8863\u670D\u5C31\u662F\uFF1A\u5367\u69FD\uFF0C\u8FD9\u4EF6\u8863\u670D\u80AF\u5B9A\u4ED6\u5988\u8D3C\u8D35\uFF01</li></ul></li><li>\u751F\u6001\u5149\u5B66\u7406\u8BBA\uFF1A\u5F3A\u8C03\u73AF\u5883</li></ul></li><li>\u7279\u6027 <ul><li>\u5BF9\u8C61\u6027\uFF1A\u77E5\u89C9\u5230\u7684\u662F\u5404\u79CD\u611F\u89C9\u4FE1\u606F\u6574\u5408\u4E4B\u540E\u7684\u5B8C\u6574\u5BF9\u8C61</li><li>\u6574\u4F53\u6027\uFF1A\u77E5\u89C9\u662F\u4E00\u4E2A\u6709\u7EC4\u7EC7\u7684\u6574\u4F53</li><li>\u7406\u89E3\u6027\uFF1A\u8BC6\u522B\u4FE1\u606F\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4F1A\u7528\u81EA\u5DF1\u7684\u7ECF\u9A8C\u8FDB\u884C\u52A0\u5DE5</li><li>\u6052\u5E38\u6027\uFF1A\u77E5\u89C9\u4E0D\u968F\u5BF9\u8C61\u7684\u7269\u7406\u7279\u6027\u7684\u6539\u53D8\u800C\u6539\u53D8</li></ul></li></ul><h1 id="\u4FE1\u606F\u7684\u5B58\u50A8\u4E0E\u52A0\u5DE5" tabindex="-1">\u4FE1\u606F\u7684\u5B58\u50A8\u4E0E\u52A0\u5DE5</h1><h2 id="\u8BB0\u5FC6" tabindex="-1">\u8BB0\u5FC6</h2><ul><li>\u6982\u8FF0 <ul><li>\u5B9A\u4E49\uFF1A\u8BB0\u5FC6\u662F\u4E2A\u4F53\u5BF9\u5176\u7ECF\u9A8C\u7684\u8BC6\u522B\uFF0C\u4FDD\u6301\u548C\u518D\u73B0</li><li>\u5206\u7C7B <ul><li>\u4E8B\u7269\u6027\u8D28\uFF1A\u60C5\u666F/\u8BED\u4E49</li><li>\u4FE1\u606F\u79CD\u7C7B\uFF1A\u7A0B\u5E8F\u6027/\u9648\u8FF0\u6027</li><li>\u63D0\u53D6\u65B9\u5F0F\uFF1A\u6709\u610F\u8BC6/\u65E0\u610F\u8BC6</li><li>\u65F6\u95F4\u957F\u77ED\uFF1A\u77AC\u65F6/\u5DE5\u4F5C/\u957F\u65F6</li></ul></li></ul></li><li>\u8BB0\u5FC6\u7684\u8111\u7ED3\u6784 <ul><li>\u6709\u610F\u8BC6 <ul><li>\u4E8B\u4EF6&lt;==&gt;\u5185\u4FA7\u989E(nie)\u53F6\u95F4\u8111</li></ul></li><li>\u65E0\u610F\u8BC6 <ul><li>\u4E60\u60EF &lt;==&gt;\u7EB9\u72B6\u4F53</li><li>\u542F\u52A8\u6548\u5E94 &lt;==&gt;\u65B0\u76AE\u5C42</li><li>\u6761\u4EF6\u53CD\u5C04 &lt;==&gt;\u5C0F\u8111&amp;\u674F\u4EC1\u6838</li><li>\u975E\u8054\u60F3\u5B66\u4E60&lt;==&gt;\u53CD\u5C04\u901A\u8DEF</li></ul></li></ul></li><li>\u57FA\u672C\u8FC7\u7A0B <ul><li>\u8BC6\u522B\uFF1A\u611F\u89C9\u8F93\u5165</li><li>\u4FDD\u6301\uFF1A\u4FE1\u606F\u7F16\u7801\uFF0C\u7406\u89E3\u5E76\u5B58\u50A8</li><li>\u63D0\u53D6\uFF1A\u518D\u73B0(\u590D\u4E60)</li></ul></li><li>\u9057\u5FD8 <ul><li>\u5B9A\u4E49\uFF1A\u5BF9\u8BB0\u5FC6\u8FC7\u5F97\u4E8B\u7269\u4E0D\u80FD\u8FDB\u884C\u63D0\u53D6</li><li>\u53D1\u751F\u5728\u7F16\u7801\u65F6\u671F(\u4FDD\u6301\u9636\u6BB5)\u548C\u63D0\u53D6\u9636\u6BB5</li><li>\u827E\u5BBE\u6D69\u65AF\u9057\u5FD8\u66F2\u7EBF\uFF1A\u4EBA\u7C7B\u7684\u9057\u5FD8\u5148\u5FEB\u540E\u6162</li><li>\u5F71\u54CD\u56E0\u7D20 <ul><li>\u6750\u6599\u7684\u6027\u8D28\u4E0E\u6570\u91CF</li><li>\u5B66\u4E60\u7684\u7A0B\u5EA6\u4E0E\u6001\u5EA6</li></ul></li><li>\u76F8\u5173\u7406\u8BBA\uFF1A\u8870\u9000\u8BF4(\u4E0D\u5F3A\u5316)\uFF0C\u5E72\u6270\u8BF4(\u6536\u5230\u5E72\u6270)\uFF0C\u538B\u6291\u8BF4(\u52A8\u529B\u4E0D\u8DB3)\uFF0C\u63D0\u53D6\u56F0\u96BE</li></ul></li></ul><h2 id="\u601D\u7EF4\u4E0E\u89E3\u51B3\u95EE\u9898" tabindex="-1">\u601D\u7EF4\u4E0E\u89E3\u51B3\u95EE\u9898</h2><ul><li>\u601D\u7EF4 <ul><li>\u5B9A\u4E49\uFF1A\u501F\u52A9\u8BED\u8A00\uFF0C\u8868\u8C61\u6216\u52A8\u4F5C\u5B9E\u73B0\u7684\uFF0C\u5BF9\u5BA2\u89C2\u4E8B\u7269\u7684\u6982\u62EC\u548C\u89C1\u89E3\u7684\u8BA4\u8BC6\uFF0C\u662F\u8BA4\u77E5\u7684\u9AD8\u7EA7\u5F62\u5F0F\u3002</li><li>\u91CD\u8981\u7279\u5F81 <ul><li>\u6982\u62EC\u6027</li><li>\u95F4\u63A5\u6027</li><li>\u5BF9\u7ECF\u9A8C\u7684\u6539\u7EC4</li></ul></li><li>\u5206\u7C7B <ul><li>\u5F62\u8C61&amp;\u903B\u8F91</li><li>\u7ECF\u9A8C&amp;\u7406\u8BBA</li><li>\u76F4\u89C9&amp;\u601D\u7EF4</li><li>\u4E13\u6CE8&amp;\u53D1\u6563</li><li>\u5E38\u89C4&amp;\u521B\u9020</li></ul></li></ul></li><li>\u6982\u5FF5 <ul><li>\u5B9A\u4E49\uFF1A\u4EBA\u8111\u53CD\u6620\u4E8B\u7269\u672C\u8D28\u7684\u4E00\u79CD\u601D\u7EF4\u65B9\u5F0F\uFF0C\u662F\u601D\u7EF4\u7684\u6700\u57FA\u672C\u5355\u4F4D</li><li>\u5185\u6DB5\uFF1A\u6982\u5FF5\u7684\u8D28\uFF0C\u53CD\u6620\u4E8B\u7269\u7684\u672C\u8D28\u7279\u5F81</li><li>\u5916\u5EF6\uFF1A\u6982\u5FF5\u7684\u91CF\uFF0C\u53CD\u6620\u6982\u5FF5\u7684\u8303\u56F4</li><li>\u5206\u7C7B <ul><li>\u5177\u4F53/\u62BD\u8C61</li><li>\u81EA\u7136/\u4EBA\u5DE5</li><li>\u5408\u53D6\u6982\u5FF5(\u6BDB\u7B14)/\u6790\u53D6\u6982\u5FF5(\u597D\u5B66\u751F)/\u5173\u7CFB\u6982\u5FF5(\u4E0A\u4E0B)</li></ul></li><li>\u6982\u5FF5\u7684\u7ED3\u6784\u7406\u8BBA <ul><li>\u5C42\u6B21\u7F51\u7EDC</li><li>\u7279\u5F81\u8868</li><li>\u539F\u578B</li></ul></li></ul></li><li>\u89E3\u51B3\u95EE\u9898 <ul><li>\u5B9A\u4E49\uFF1A\u4E00\u4E9B\u5217\u6709\u76EE\u7684\uFF0C\u6709\u6307\u5411\u7684\u8BA4\u77E5\u64CD\u4F5C\u8FC7\u7A0B\u3002</li><li>\u7B56\u7565 <ul><li>\u7B97\u6CD5</li><li>\u542F\u53D1\u6CD5 <ul><li>\u624B\u6BB5-\u76EE\u7684\u5206\u6790</li><li>\u9006\u5411</li></ul></li></ul></li><li>\u5F71\u54CD\u56E0\u7D20 <ul><li>\u77E5\u8BC6\u7684\u8868\u5F81\u65B9\u5F0F</li><li>\u601D\u7EF4\u5B9A\u52BF</li></ul></li></ul></li></ul>',10),_=[a];function o(n,d,s,c,r,h){return u(),i("div",null,_)}var g=l(e,[["render",o]]);export{m as __pageData,g as default};
