import{_ as l,c as i,o as e,a as t}from"./app.a51640e9.js";const u='{"title":"\u62D6\u5EF6\u75C7","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48\u8981\u6CBB\u7597\u62D6\u5EF6\u75C7","slug":"\u4E3A\u4EC0\u4E48\u8981\u6CBB\u7597\u62D6\u5EF6\u75C7"},{"level":2,"title":"\u53D1\u751F\u7684\u539F\u56E0","slug":"\u53D1\u751F\u7684\u539F\u56E0"},{"level":2,"title":"\u7B80\u5355\u7684\u89E3\u51B3\u65B9\u6CD5","slug":"\u7B80\u5355\u7684\u89E3\u51B3\u65B9\u6CD5"},{"level":3,"title":"\u8FC7\u7A0B&\u7ED3\u679C","slug":"\u8FC7\u7A0B-\u7ED3\u679C"},{"level":3,"title":"\u50F5\u5C38\u6A21\u5F0F","slug":"\u50F5\u5C38\u6A21\u5F0F"},{"level":3,"title":"\u5E94\u5BF9\u5B66\u4E60\u548C\u751F\u6D3B","slug":"\u5E94\u5BF9\u5B66\u4E60\u548C\u751F\u6D3B"},{"level":3,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3"},{"level":3,"title":"\u6DF1\u5165\u63A2\u7D22\u8BB0\u5FC6","slug":"\u6DF1\u5165\u63A2\u7D22\u8BB0\u5FC6"},{"level":3,"title":"\u957F\u671F\u8BB0\u5FC6","slug":"\u957F\u671F\u8BB0\u5FC6"},{"level":3,"title":"\u5206\u7EC4\u4E0E\u8BB0\u5FC6\u5BAB\u6BBF","slug":"\u5206\u7EC4\u4E0E\u8BB0\u5FC6\u5BAB\u6BBF"}],"relativePath":"MOOC/how-to-learn/procrastination-memory.md"}',o={},p=t('<h1 id="\u62D6\u5EF6\u75C7" tabindex="-1">\u62D6\u5EF6\u75C7</h1><h2 id="\u4E3A\u4EC0\u4E48\u8981\u6CBB\u7597\u62D6\u5EF6\u75C7" tabindex="-1">\u4E3A\u4EC0\u4E48\u8981\u6CBB\u7597\u62D6\u5EF6\u75C7</h2><ol><li>\u60F3\u8981\u5C06\u5DE5\u4F5C\u8BB0\u5FC6\u53D8\u4E3A\u957F\u671F\u8BB0\u5FC6\u5FC5\u987B\u8981\u514B\u670D\u62D6\u5EF6\u75C7</li><li>\u53EF\u80FD\u77ED\u671F\u7684\u62D6\u5EF6\u75C7\u4F1A\u611F\u53D7\u5230\u5341\u8DB3\u7684\u5FEB\u4E50\u3002\u4F46\u957F\u671F\u4EE5\u6765\u5B83\u5C31\u4F1A\u5BFC\u81F4\u6781\u574F\u7684\u7ED3\u679C\uFF0C\u4ED6\u53EF\u80FD\u4F1A\u5728\u4E00\u573A\u8003\u8BD5\u4E2D\u7206\u53D1\u6216\u8005\u4EC0\u4E48\u5176\u4ED6\u7684\u3002</li></ol><h2 id="\u53D1\u751F\u7684\u539F\u56E0" tabindex="-1">\u53D1\u751F\u7684\u539F\u56E0</h2><p>\u9996\u5148\uFF0C\u62D6\u5EF6\u75C7\u662F\u4E00\u79CD\u4E60\u60EF\u3002\u800C\u4E60\u60EF\u7684\u5F62\u6210\u662F\u6709\u4E00\u4E2A\u8FC7\u7A0B\u7684\u3002</p><ol><li>\u4FE1\u53F7\uFF0C\u8FD9\u662F\u4F60\u8FDB\u5165\u201C\u50F5\u5C38\u72B6\u6001\u201D\u7684\u89E6\u53D1\u5668</li><li>\u60EF\u5F0F\uFF0C\u4F60\u7684\u5927\u8111\u5728\u63A5\u6536\u7EBF\u7D22\u65F6\u505A\u51FA\u7684\u4E60\u60EF\u53CD\u5E94\u5C31\u662F\u60EF\u5F0F</li><li>\u5956\u52B1\uFF0C\u4EFB\u4F55\u4E00\u79CD\u4E60\u60EF\u5F97\u4EE5\u53D1\u5C55\u548C\u5EF6\u7EED\u90FD\u662F\u56E0\u4E3A\u5B83\u53EF\u4EE5\u56DE\u62A5\u6211\u4EEC\uFF0C\u5B83\u4F1A\u7ACB\u5373\u7ED9\u6211\u4EEC\u5E26\u6765\u4E9B\u8BB8\u6109\u60A6\u611F\u3002</li><li>\u4FE1\u5FF5\uFF0C\u575A\u4FE1\u4E60\u60EF\uFF0C\u4E60\u60EF\u5C31\u4F1A\u5F3A\u5927\u8D77\u6765\u3002</li></ol><p>\u800C\u62D6\u5EF6\u75C7\u5728\u7B2C\u4E09\u9636\u6BB5\u4F1A\u76F8\u8F83\u4E8E\u5DE5\u4F5C\u6765\u8BF4\u66F4\u5FEB\u5730\u83B7\u5F97\u5FEB\u611F\u3002\u5E76\u4E14\u4F60\u81EA\u5DF1\u4F1A\u5728\u7B2C\u56DB\u9636\u6BB5\u627E\u5230\u4E00\u4E2A\u6B3A\u9A97\u81EA\u5DF1\u7684\u7406\u7531\u7528\u4EE5\u652F\u6491\u62D6\u5EF6\u75C7\uFF0C\u4F8B\u5982\uFF0C\u4F5C\u4E1A\u5F88\u5FEB\u5C31\u80FD\u505A\u5B8C\uFF0C\u5176\u5B9E\u4F60\u660E\u767D\u662F\u8981\u82B1\u5F88\u957F\u65F6\u95F4\u7684\u3002</p><h2 id="\u7B80\u5355\u7684\u89E3\u51B3\u65B9\u6CD5" tabindex="-1">\u7B80\u5355\u7684\u89E3\u51B3\u65B9\u6CD5</h2><h3 id="\u8FC7\u7A0B-\u7ED3\u679C" tabindex="-1">\u8FC7\u7A0B&amp;\u7ED3\u679C</h3><p>\u4E3A\u4E86\u5BF9\u6297\u62D6\u5EF6\u75C7\uFF0C\u4F60\u9700\u8981\u77E5\u9053\u4F60\u771F\u6B63\u9700\u8981\u5173\u6CE8\u7684\u662F\u4EC0\u4E48\u3002\u76F8\u8F83\u4E8E\u5173\u6CE8\u7ED3\u679C\uFF0C\u5173\u6CE8\u8FC7\u7A0B\u4F1A\u66F4\u52A0\u6709\u6548\u3002</p><p>\u8FC7\u7A0B\u6307\u6D41\u52A8\u7684\u65F6\u95F4\u4EE5\u53CA\u8FD9\u6BB5\u65F6\u95F4\u91CC\u7684\u4E60\u60EF\u548C\u884C\u4E3A\u3002\u7ED3\u679C\u4F1A\u89E6\u53D1\u75DB\u82E6\uFF0C\u5BFC\u81F4\u4F60\u4E0D\u60F3\u53BB\u5B8C\u6210\u3002\u5F53\u7136\u5728\u8FC7\u7A0B\u4E2D\u4F60\u4E5F\u53EF\u4EE5\u51CF\u5C11\u5916\u754C\u7684\u5E72\u6270\u4EE5\u4FBF\u4E8E\u66F4\u52A0\u4E13\u6CE8\u3002</p><h3 id="\u50F5\u5C38\u6A21\u5F0F" tabindex="-1">\u50F5\u5C38\u6A21\u5F0F</h3><p>\u5BF9\u4ED8\u574F\u4E60\u60EF\u7684\u6709\u6548\u65B9\u6CD5\u662F\u6539\u53D8\u5B83\uFF0C\u4E3A\u6B64\u4F60\u9700\u8981\u4F7F\u7528\u201C\u50F5\u5C38\u6A21\u5F0F\u201D\u6765\u5E2E\u52A9\u4F60\u3002\u6211\u4EEC\u4ECE\u4E60\u60EF\u7684\u5F62\u6210\u5F00\u59CB\u5206\u6790\u3002</p><ol><li>\u4FE1\u53F7\uFF0C\u786E\u8BA4\u662F\u4EC0\u4E48\u8BA9\u4F60\u8FDB\u5165\u5230\u50F5\u5C38\u62D6\u5EF6\u6A21\u5F0F\u3002\u4FE1\u53F7\u901A\u5E38\u5206\u4E3A\u56DB\u7C7B\uFF1A\u65F6\u95F4\uFF0C\u5730\u70B9\uFF0C\u611F\u53D7\u548C\u53CD\u5E94\u3002</li><li>\u60EF\u5F0F\uFF0C\u4F60\u9700\u8981\u91CD\u7F6E\u62D6\u5EF6\u7684\u60EF\u5F0F\uFF0C\u5176\u5173\u952E\u662F\u5236\u5B9A\u4E00\u4E2A\u8BA1\u5212\uFF0C\u6446\u8131\u4F7F\u4F60\u62D6\u5EF6\u7684\u4FE1\u53F7\uFF0C\u8BB0\u5F97\u53CA\u65F6\u68C0\u67E5\u81EA\u5DF1\u7684\u8BA1\u5212\uFF0C\u53EF\u80FD\u4ED6\u5728\u77ED\u65F6\u95F4\u5185\u4E0D\u4F1A\u751F\u6548\uFF0C\u4F46\u8BF7\u575A\u6301\u4E0B\u53BB\u3002</li><li>\u5956\u52B1\uFF0C\u4E60\u60EF\u7684\u529B\u91CF\u4E4B\u6240\u4EE5\u5F3A\u5927\uFF0C\u662F\u56E0\u4E3A\u4E60\u60EF\u5236\u9020\u4E86\u795E\u7ECF\u7CFB\u7EDF\u7684\u6B32\u671B\u3002\u52A0\u4E0A\u4E00\u4E2A\u65B0\u7684\u5956\u52B1\u53EF\u4EE5\u5E2E\u52A9\u4F60\u514B\u670D\u539F\u5148\u7684\u6B32\u671B\uFF0C\u53EA\u6709\u5F53\u5927\u8111\u5F00\u59CB\u671F\u5F85\u90A3\u4E2A\u5956\u52B1\uFF0C\u65E7\u4E60\u60EF\u624D\u80FD\u5F97\u5230\u91CD\u7F6E\uFF0C\u4F60\u624D\u80FD\u517B\u6210\u65B0\u4E60\u60EF\u3002</li><li>\u4FE1\u5FF5\uFF0C\u6539\u53D8\u62D6\u5EF6\u4E60\u60EF\u6700\u91CD\u8981\u7684\u4E00\u70B9\u5C31\u662F\uFF0C\u76F8\u4FE1\u4F60\u80FD\u591F\u505A\u5230\u7684\u8FD9\u4E2A\u4FE1\u5FF5\u3002\u8FD9\u4E00\u70B9\u5728\u4F60\u6000\u7591\u81EA\u5DF1\u7684\u65F6\u5019\u6781\u4E3A\u91CD\u8981\u3002</li></ol><h3 id="\u5E94\u5BF9\u5B66\u4E60\u548C\u751F\u6D3B" tabindex="-1">\u5E94\u5BF9\u5B66\u4E60\u548C\u751F\u6D3B</h3><ol><li>\u5BF9\u5927\u591A\u6570\u4EBA\u6765\u8BF4\uFF0C\u5B66\u4E60\u9700\u8981\u5728\u4E0D\u540C\u7684\u4EFB\u52A1\u4E2D\u8FBE\u5230\u5FAE\u5999\u7684\u5E73\u8861\u3002\u8981\u4FDD\u6301\u5BF9\u5B66\u4E60\u76EE\u6807\u7684\u5C55\u671B\uFF0C\u4E00\u4E2A\u5F88\u597D\u7684\u529E\u6CD5\u662F\u6BCF\u5468\u5728\u8BA1\u5212\u672C\u91CC\u5199\u4E0B\u672C\u5468\u5173\u952E\u4EFB\u52A1\u5217\u8868\uFF0C\u6BCF\u4E00\u5929\u53E6\u8D77\u4E00\u9875\uFF0C\u5199\u4E0B\u5408\u7406\u7684\u4EFB\u52A1\u5217\u8868\u3002</li><li>\u8BA1\u5212\u505C\u6B62\u5DE5\u4F5C\u7684\u65F6\u95F4\u4E0E\u8BA1\u5212\u5DE5\u4F5C\u7684\u65F6\u95F4\u4E00\u6837\u91CD\u8981\u3002\u7ED9\u81EA\u5DF1\u5728\u524D\u4E00\u5929\u665A\u4E0A\u5236\u5B9A\u76EE\u6807\uFF0C\u5E76\u8BBE\u5B9A\u4E00\u4E2A\u65F6\u95F4\u671F\u9650(\u4F8B\u5982\u4E0B\u5348 5 \u70B9)\u3002\u5728\u4E8B\u4EF6\u671F\u9650\u5185\u5B8C\u6210\u5B83\u3002</li><li>\u4F60\u7684\u751F\u6D3B\u672C\u8EAB\uFF0C\u5E76\u4E0D\u4F1A\u81EA\u5DF1\u5728\u65F6\u95F4\u8868\u4E0A\u817E\u51FA\u4F11\u606F\u548C\u7A7A\u95F2\u7684\u65F6\u95F4\uFF0C\u7136\u800C\u4E0D\u8BBA\u4F60\u7684\u751F\u6D3B\u5982\u4F55\uFF0C\u8BD5\u7740\u6324\u51FA\u70B9\u4F11\u606F\u65F6\u95F4\u5427\u3002</li></ol><h3 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3</h3><ol><li><strong>\u575A\u6301\u5199\u8BA1\u5212\u65E5\u5FD7\u65B9\u4FBF\u8FFD\u8E2A\u4F55\u65F6\u5B8C\u6210\u76EE\u6807\u4EE5\u53CA\u68C0\u67E5\u8BA1\u5212\u662F\u5426\u6709\u6548</strong></li><li>\u6BCF\u5929\u7ED9\u81EA\u5DF1\u89C4\u5B9A\u4E00\u4E9B\u65E5\u5E38\u4EFB\u52A1\uFF0C\u63D0\u524D\u4E00\u665A\u5236\u5B9A\u8BA1\u5212\uFF0C\u5C06\u4F60\u7684\u5DE5\u4F5C\u5212\u5206\u6210\u4E00\u7CFB\u5217\u5C0F\u4EFB\u52A1\uFF0C\u786E\u4FDD\u201C\u50F5\u5C38\u6A21\u5F0F\u201D\u53EF\u4EE5\u83B7\u5F97\u5956\u52B1</li><li>\u5EF6\u8FDF\u5956\u52B1\u5230\u4F60\u5B8C\u6210\u4E86\u8FD9\u4E2A\u4EFB\u52A1(\u4E70\u6B4C)</li><li>\u7559\u610F\u4F7F\u4F60\u62D6\u5EF6\u7684\u4FE1\u53F7\uFF0C\u5C3D\u91CF\u51CF\u5C11\u8FD9\u79CD\u4FE1\u53F7\u7684\u51FA\u73B0(\u6253\u5F00 B \u7AD9)</li><li>\u76F8\u4FE1\u4F60\u81EA\u5DF1\uFF0C\u8FD9\u6837\u624D\u4F1A\u5728\u4F11\u606F\u65F6\u65E0\u7F6A\u6076\u611F</li><li>\u5F53\u4F60\u4ECD\u7136\u62D6\u5EF6\u65F6\u5236\u5B9A\u5907\u7528\u8BA1\u5212</li><li><strong>\u6BCF\u5929\u5148\u5904\u7406\u56F0\u96BE\u7684\u4EFB\u52A1</strong></li></ol><h1 id="\u8BB0\u5FC6" tabindex="-1">\u8BB0\u5FC6</h1><p>\u8BA8\u8BBA\u4E86\u4E24\u79CD\u4E0E\u6982\u5FF5\u7EC4\u5757\u5316\u7684\u4E24\u79CD\u8BB0\u5FC6\u7CFB\u7EDF\uFF1A\u957F\u671F\u8BB0\u5FC6\u548C\u5DE5\u4F5C\u8BB0\u5FC6\u3002</p><p>\u957F\u671F\u8BB0\u5FC6\uFF1A\u5B83\u5C31\u50CF\u4E2A\u5B58\u50A8\u4ED3\u5E93\uFF0C\u4F60\u9700\u8981\u7EC3\u4E60\u548C\u91CD\u590D, \u4EE5\u5E2E\u52A9\u5B58\u50A8\uFF0C\u4EE5\u6B64\u5C06\u201C\u8D27\u54C1\u201D\u5B58\u5165\u957F\u671F\u8BB0\u5FC6\u4ED3\u5E93\uFF0C\u4FBF\u4E8E\u53D6\u7528\uFF0C\u4E0D\u8981\u5BC4\u5E0C\u671B\u4E8E\u4E00\u5929\u5185\u7EC3\u4E60\u5E76\u91CD\u590D\uFF0C\u4F60\u6700\u597D\u5C06\u7EC3\u4E60\u5206\u6210\u51E0\u5929\u5B8C\u6210\uFF0C\u89E3\u51B3\u62D6\u5EF6\u7684\u91CD\u8981\u6027\u4FBF\u5728\u4E8E\u6B64\u3002</p><p>\u5DE5\u4F5C\u8BB0\u5FC6\uFF1A\u5B83\u5C31\u50CF\u4E2A\u52A3\u8D28\u9ED1\u677F \u4E0A\u9762\u7684\u5B57\u8FF9\u96BE\u4EE5\u4E45\u7559\uFF0C\u5DE5\u4F5C\u8BB0\u5FC6\u91CC\u53EA\u80FD\u5B58\u653E\u56DB\u9879\u5185\u5BB9\u3002\u5F53\u4F60\u5728\u67D0\u79CD\u610F\u4E49\u4E0A\u638C\u63E1\u4E00\u9879\u6280\u672F\u6216\u4E00\u4E2A\u6982\u5FF5\u65F6\uFF0C\u5B83\u4F1A\u5C06\u8FD9\u4E9B\u5185\u5BB9\u538B\u7F29\uFF0C\u8FD9\u6837\u653E\u5165\u5927\u8111\u65F6\u5360\u7528\u7684\u5DE5\u4F5C\u8BB0\u5FC6\u7A7A\u95F4\u66F4\u5C0F\uFF0C\u5982\u6B64\u4F1A\u91CA\u653E\u66F4\u591A\u7CBE\u795E\u601D\u8003\u7A7A\u95F4\uFF0C\u4EE5\u6B64\u66F4\u5BB9\u6613\u7684\u5904\u7406\u5176\u4ED6\u7684\u60F3\u6CD5\u3002</p><h3 id="\u6DF1\u5165\u63A2\u7D22\u8BB0\u5FC6" tabindex="-1">\u6DF1\u5165\u63A2\u7D22\u8BB0\u5FC6</h3><p>\u4F60\u7684\u5927\u8111\u5929\u751F\u5C31\u5177\u6709\u5F3A\u5927\u7684\u89C6\u89C9\u8BB0\u5FC6\u7CFB\u7EDF\uFF0C\u64C5\u957F\u8BB0\u5FC6\u4E0D\u540C\u5730\u70B9\u7684\u6982\u51B5\u3002\u56E0\u4E3A\u6211\u4EEC\u7684\u7956\u5148\u5728\u6253\u730E\u4E4B\u540E\u9700\u8981\u8BB0\u5FC6\u56DE\u5BB6\u7684\u8DEF\uFF0C\u8FD9\u662F\u8FDB\u5316\u7684\u9700\u8981\u3002</p><p>\u56FE\u50CF\u5BF9\u8BB0\u5FC6\u5982\u6B64\u91CD\u8981\u7684\u90E8\u5206\u539F\u56E0\uFF0C\u662F\u56FE\u50CF\u4E0E\u53F3\u8111\u7684\u89C6\u89C9\u7A7A\u95F4\u4E2D\u67A2\u76F4\u63A5\u76F8\u8FDE\u3002\u901A\u8FC7\u6316\u6398\u89C6\u89C9\u533A\u57DF, \u56FE\u50CF\u5E2E\u52A9\u4F60\u66F4\u597D\u7684\u6355\u6349\u770B\u4F3C\u5355\u8C03, \u5F88\u96BE\u8BB0\u4F4F\u7684\u6982\u5FF5\uFF0C\u4EE5\u6B64\u63D0\u9AD8\u4F60\u7684\u8BB0\u5FC6\u80FD\u529B \u3002\u6FC0\u6D3B\u5404\u79CD\u611F\u5B98\u800C\u5EFA\u7ACB\u7684\u795E\u7ECF\u94A9\u8D8A\u591A\uFF0C\u4F60\u5C31\u8D8A\u5BB9\u6613\u8BB0\u4F4F\u67D0\u4E2A\u6982\u5FF5\u548C\u5176\u542B\u4E49\u3002</p><p>\u91CD\u590D\u8BAD\u7EC3\u5F88\u91CD\u8981\u3002\u5373\u4F7F\u4F60\u521B\u9020\u7684\u5185\u5BB9\u4EE4\u4EBA\u96BE\u5FD8\uFF0C\u91CD\u590D\u8BAD\u7EC3\u4E5F\u6709\u52A9\u4E8E\u5B83\u7262\u7262\u5D4C\u5165\u957F\u65F6\u8BB0\u5FC6\u4E2D\u3002\u8BB0\u5F97\u4E00\u5929\u8981\u91CD\u590D\u8BAD\u7EC3\u591A\u6B21\uFF0C\u4F46\u5728\u4E4B\u540E\u51E0\u5929\u8981\u65E0\u89C4\u5219\u7684\u91CD\u590D\u3002\u7D22\u5F15\u5361\u5E38\u5E38\u5F88\u6709\u7528\uFF0C\u56E0\u4E3A\u624B\u5199\u8BF5\u8BFB\u8981\u5B66\u7684\u4E1C\u897F\u4F3C\u4E4E\u4E5F\u6709\u52A9\u4E8E\u589E\u5F3A\u8BB0\u5FC6\u3002</p><h3 id="\u957F\u671F\u8BB0\u5FC6" tabindex="-1">\u957F\u671F\u8BB0\u5FC6</h3><p>\u65E0\u8BBA\u4F60\u4F55\u65F6\u91CD\u542F\u8BB0\u5FC6\uFF0C\u8BB0\u5FC6\u90FD\u4F1A\u6539\u53D8\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u79F0\u4F5C\u518D\u5DE9\u56FA\u3002\u53EF\u80FD\u8FD8\u4F1A\u5728\u5EFA\u8BAE\u4E0E\u60F3\u8C61\u7684\u4F5C\u7528\u4E0B\u690D\u5165\u9519\u8BEF\u7684\u8BB0\u5FC6\uFF0C\u4E0E\u65E7\u7684\u8BB0\u5FC6\u5927\u4E0D\u76F8\u540C\uFF0C\u5C24\u5176\u662F\u9488\u5BF9\u62E5\u6709\u751F\u52A8\u60F3\u8C61\u529B\u7684\u513F\u7AE5\u3002</p><h3 id="\u5206\u7EC4\u4E0E\u8BB0\u5FC6\u5BAB\u6BBF" tabindex="-1">\u5206\u7EC4\u4E0E\u8BB0\u5FC6\u5BAB\u6BBF</h3><p>\u5206\u7EC4\uFF1A\u5C06\u4E00\u7EC4\u6982\u5FF5\u878D\u5408\u6210\u4E00\u5F20\u6709\u8DA3\u7684\u753B\u6216\u4E00\u7EC4\u6ED1\u7A3D\u7684\u52A8\u4F5C\uFF0C\u8D8A\u6709\u8DA3\u8D8A\u5BB9\u6613\u8BB0\u5FC6\u3002</p><p>\u8BB0\u5FC6\u5BAB\u6BBF\u6CD5\uFF1A\u5C06\u6709\u52A9\u4E8E\u8BB0\u5FC6\u7684\u5F62\u8C61\u5D4C\u5165\u4F60\u719F\u6089\u7684\u573A\u666F\u4E2D\uFF0C\u8BA9\u81EA\u5DF1\u611F\u53D7\u89C6\u89C9\u8BB0\u5FC6\u7CFB\u7EDF\u8D4B\u4E88\u7684\u529B\u91CF\u3002\u6311\u9009\u4E00\u4E2A\u719F\u6089\u7684\u623F\u5B50\uFF0C\u5C06\u5176\u4E2D\u7684\u7269\u54C1\u8BB0\u5FC6\u6210\u7406\u89E3\u7684\u77E5\u8BC6\u70B9\u4E5F\u53EF\u4EE5\u6839\u636E\u5BF9\u5E94\u77E5\u8BC6\u70B9\u7684\u7279\u70B9\u8FDB\u884C\u9B54\u5316\uFF0C\u8FD9\u5BF9\u8BB0\u5FC6\u4E92\u4E0D\u5173\u8054\u7684\u7269\u54C1\u5F88\u6709\u6548\u3002</p><p>\u5B66\u7740\u4EE5\u66F4\u52A0\u8BAD\u7EC3\u6709\u7D20\u4E14\u522B\u5177\u521B\u610F\u7684\u65B9\u5F0F\u6765\u4F7F\u7528\u8BB0\u5FC6\uFF0C\u4F1A\u6709\u52A9\u4E8E\u96C6\u4E2D\u6CE8\u610F\u529B\uFF0C\u5C31\u7B97\u4E3A\u4E86\u589E\u5F3A\u8BB0\u5FC6\u800C\u521B\u9020\u51FA\u5929\u9A6C\u884C\u7A7A\u7684\u5173\u8054\u4E5F\u6CA1\u5173\u7CFB\u3002</p>',32),a=[p];function n(d,s,h,r,_,c){return e(),i("div",null,a)}var x=l(o,[["render",n]]);export{u as __pageData,x as default};
